module.exports = function (htmlSnapshot, searchEngineDetector, espacedFragmentUtilities) {
    var snapshot = function (url, res, next) {
        htmlSnapshot.snapshot(url, function (err, html) {
            if (err) {
                next();
            } else {
                res.send(html);
            }
        });
    };

    var useSnapshot = function (req) {
        return espacedFragmentUtilities.isEscapedRequest(req) || searchEngineDetector.isSearchEngineBot(req.headers['user-agent']);
    };

    var getUrlForSnapshot = function (req) {
        var url;
        if (espacedFragmentUtilities.isEscapedRequest(req)) {
            url = espacedFragmentUtilities.unescapeRequest(req);
        } else {
            url = req.protocol + "://" + req.headers.host + req.originalUrl;
        }
        return url;
    };

    return {
        execute: function (req, res, next) {
            if (useSnapshot(req)) {
                var url = getUrlForSnapshot(req);
                snapshot(url, res, next);
            } else {
                next();
            }
        }
    }
};
